import { Button } from '@mui/material'
import Drawer from '@mui/material/Drawer' 
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import React,{ createContext, useContext, useState } from 'react';
import { ToggleButton, ToggleButtonGroup } from '@mui/material';
import { useSideMenu } from '../App';


export default function SideMenu() {
	const [selectedItem, setSelectedItem] = useSideMenu();

	const [open, setOpen] = React.useState(false);

	const handleDrawerOpen = () => {
		setOpen(true);
	};
	const handleDrawerClose = () => {
		setOpen(false);
	};

    const handleSelect = (event, newSelected) => {
        setSelected(newSelected);
    };

	return (
		<div>
			<IconButton onClick={handleDrawerOpen}>
				<MenuIcon/>		
			</IconButton>
			<Drawer
				open={open}
				anchor='right'
				variant='persistent'
			>
				<div>
					<IconButton onClick={handleDrawerClose}>
						<ChevronLeftIcon fontSize='large' />
					</IconButton>
				</div>
				<ToggleButtonGroup 
					className='nav-items-container'
					value={selectedItem}
					exclusive // このプロパティで一つだけ選択できるようにする
					onChange={handleSelect}
					aria-label="single selection"
				>
					<ToggleButton value="option1" aria-label="Option 1">
						Option 1
					</ToggleButton>
					<ToggleButton value="option2" aria-label="Option 2">
						Option 2
					</ToggleButton>
					<ToggleButton value="option3" aria-label="Option 3">
						Option 3
					</ToggleButton>				
				</ToggleButtonGroup>
			</Drawer>
		</div>
	)
}
