import logo from './logo.svg';
import SideMenu from './component/SideMenu';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';

function App() {
  const position = [51.505, -0.09];

  var endpoint = "https://query.wikidata.org/sparql";
  var query = (function () {/*
  SELECT ?countryLabel ?lat ?long ?population
  WHERE {
    ?country wdt:P31 wd:Q6256;                
            wdt:P1082 ?population;           
            wdt:P625 ?location.             
    BIND(geof:latitude(?location) AS ?lat)
    BIND(geof:longitude(?location) AS ?long)
    SERVICE wikibase:label { bd:serviceParam wikibase:language "ja". }
  }
  */}).toString().match(/\n([\s\S]*)\n/)[1];
  // SPARQLクエリを実行してデータを取得
  fetch(endpoint + "?query=" + encodeURIComponent(query), {
    headers: { "Accept": "application/json" }  // JSON形式でのレスポンスを要求
  })
    .then(response => response.json())
    .then(data => {
        console.log(data); // クエリの結果をコンソールで確認

        // 取得したデータで各国のバブルを地図に表示
        data.results.bindings.forEach(place => {
            var lat = parseFloat(place.lat.value);
            var long = parseFloat(place.long.value);
            var population = parseInt(place.population.value);
            var radius = Math.sqrt(population) / 1000;  // バブルのサイズを調整（必要に応じて調整可能）

            // LeafletのCircleMarkerでバブルを作成
            L.circleMarker([lat, long], {
                radius: radius,
                color: "#007bff",
                fillColor: "#007bff",
                fillOpacity: opacity
            }).bindPopup(`${place.countryLabel.value}<br>Population: ${population.toLocaleString()}`)
              .addTo(map)
              .on('add', () => console.log(`Added marker for ${place.countryLabel.value}`)); // 描画確認用
        });
    })
    .catch(error => console.error("Error fetching data:", error));
  return (
    <div className="App">
      <div className="app-container">
        <div className="map-container">
          <MapContainer center={position} zoom={3} scrollWheelZoom={false}>
            <TileLayer 
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png">
            </TileLayer>
          </MapContainer>
        </div>
        <div className="nav-container">
          <SideMenu/>
        </div>
      </div>
    </div>
  );
}

export default App;
