import { Drawer, IconButton, ToggleButton, ToggleButtonGroup } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import React from 'react';
import { useSideMenu } from '../App';

export default function SideMenu() {
	const [selectedItem, setSelectedItem] = useSideMenu();
	const [open, setOpen] = React.useState(false);

	const handleDrawerOpen = () => {
		setOpen(true);
	};
	const handleDrawerClose = () => {
		setOpen(false);
	};

	const handleSelect = (event, newSelected) => {
		if (newSelected !== null) {
			setSelectedItem(newSelected);
		}
	};

	return (
		<div>
			<IconButton onClick={handleDrawerOpen}>
				<MenuIcon />
			</IconButton>
			<Drawer
				open={open}
				anchor="right"
				variant="persistent"
			>
				<div>
					<IconButton onClick={handleDrawerClose}>
						<ChevronLeftIcon fontSize="large" />
					</IconButton>
				</div>
				<ToggleButtonGroup
					value={selectedItem}
					exclusive
					onChange={handleSelect}
					aria-label="single selection"
					orientation="vertical" // 垂直方向に配置
					style={{ display: 'flex', alignItems: 'flex-start' }} // 左揃え
				>
					<ToggleButton value="option1" aria-label="Option 1" style={{ justifyContent: 'flex-start' }}>
						Option 1
					</ToggleButton>
					<ToggleButton value="option2" aria-label="Option 2" style={{ justifyContent: 'flex-start' }}>
						Option 2
					</ToggleButton>
					<ToggleButton value="option3" aria-label="Option 3" style={{ justifyContent: 'flex-start' }}>
						Option 3
					</ToggleButton>
				</ToggleButtonGroup>
			</Drawer>
		</div>
	);
}
